mapper.update:
update purchaseorder set supplierid = #{supplierid} where purchaseid = #{id} and supplierid is not null;
update purchaseorder set goodid = #{goodid} where purchaseid = #{id} and goodid is not null;
update purchaseorder set purchaseprice = #{purchaseprice} where purchaseid = #{id} and purchaseprice is not null;
update purchaseorder set purchasetime = #{purchasetime} where purchaseid = #{id} and LENGTH(trim(purchasetime)) > 0;
update purchaseorder set amount = #{amount} where purchaseid = #{id} and amount is not null;
update purchaseorder set isreturn = #{isreturn} where purchaseid = #{id} and isreturn is not null;
